$(document).ready(function() {
    dataSocialMedia()
     gatLastFiveApps()
 });
 function dataSocialMedia() {
     var label = new Array();
     var data = new Array();
     var color = new Array();
     $.ajax({
         url: globalgetSocialMedia,
         method:"GET",
         dataType:'JSON',
         contentType: false,
         cache: false,
         headers: {
             'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
         },
         processData: false,
         beforeSend: function()
         {
 
         },
         success:function(data)
         {
             console.log(data);
             var config = {
                 type: 'bar',
                 data: data,
                 scaleBeginAtZero : true,
                 options: {
                     responsive: true,
                     legend: {
                         position: 'top',
                     },
                     title: {
                         display: true,
                         text: 'Social Media'
                     },
                     animation: {
                         animateScale: true,
                         animateRotate: true
                     }
                 }
             };
 
 
             var testData = !!document.getElementById("chartjs_social");
             if (testData) {
                 var ctx = document.getElementById("chartjs_social").getContext("2d");
                 window.myDoughnut = new Chart(ctx, config);
             }
 
 
         },
         error: function (err)
         {
             console.log('err', err)
         },
         complete: function()
         {
         },
     })
 };
 function gatLastFiveApps() {
     var codigo = 1;
     var applicationsTable = $('#applicationsTable').DataTable({
         responsive: true,
         processing: true,
         serverSide: true,
         scrollX: '50px',
         "paging":   false,
         "ordering": false,
         ajax:{
             url : globalRouteLastFiveApps,
             type: "get",
             data: {"estable": codigo},
             error: function (xhr, error, thrown) {
             },
         },
         language: {
             "url": dataTablesLangEs
         },
         "columns": [
         { data: "paciente" },
         { data: "status" },
         { data: "coordinador" },
         { data: "fecha" },
         { data: "procedimiento" },
         { data: "servicio" },
         { data: "marca" },
         { data: "paquete" },
         { data: "codigo" },
 
         ],
         createdRow: function (row, data, dataIndex) {
             $(row).addClass('odd gradeX');
         },
         initComplete: function() {
             this.api().responsive.recalc()
         },
     });
 }
 